/** renders helper types for --localization flag */
export default function renderLocalizedTypes(localization: boolean): "\n    export type LocalizedField<T> = Partial<Record<LOCALE_CODE, T>>\n  \n    export interface Asset {\n        title: string\n        description: string\n        file: {\n          url: string\n          details: {\n            size: number\n            image?: {\n              width: number\n              height: number\n            }\n          }\n          fileName: string\n          contentType: string\n        }\n    }\n  " | null;
